import './App.css';
import React,{useState,useEffect} from 'react';
import Homepage from "./components/Homepage";
import Generator from './components/Generator';
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { Comic } from './components/Comic';
import { v4 as uuid } from "uuid";

const unique_id = uuid();

const newPanelObj = [
  {
    id: unique_id,
    desc: "",
    imgsrc: "",
    loading: false,
  },
];

function App() {
  const [panels, setPanels] = useState(newPanelObj);
  // const [panels, setPanels] = useState(panelLocalStorage);

//   useEffect(()=>{
//     window.localStorage.setItem("panels",JSON.stringify(panels));
// },[panels])

  return (
    <div className="appWrapper">
      <Router>
        <Routes>
          <Route path="/" element={<Homepage panels={panels} setPanels={setPanels}/>} />
          <Route path="/comic" element={<Comic panels={panels} setPanels={setPanels}  />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
